import React, { useState } from "react";
import axios from 'axios'

export default function Form() {

    // Step: State lagu keydiyo waxa form-ka lagu qoro

    const initialFormState = {
        name: "",
        population: "",
        land_area: "",

    }

    const [formState, setFormState] = useState(initialFormState)


    // Step 2: Isku xir state-ka kore iyo form inputs


    // Step 3: Samee labo function, mid ka dhig handleChange, midna handleSubmit

    const handleChange = (event) => {

        // Copy ka samee state-ka kore, kadib waxaad soo jiidataa input-ka ama qaybta aad formka ooga jirto, magaca (name) iyo value

        const inputData = {
            ...formState,

            [event.target.name]: event.target.type === "checkbox" ? event.target.checked : event.target.value
        }

        setFormState(inputData)
    }


    // Handle Submit

    const handleSubmit = (event) => {
        // Controlled Component
        event.preventDefault()

        axios.post("https://reqres.in/api/users", formState)
            .then((res) => {
                console.log(res)

                setFormState(initialFormState)
            })
            .catch((err) => console.log(err))


    }






    return (
        <div>
            <form onSubmit={handleSubmit}>

                <label htmlFor="name">
                    Name
                    <input
                        id="name"
                        type="text"
                        name="name"
                        value={formState.name}
                        onChange={handleChange}
                    />

                </label>

                <label htmlFor="email">
                    Population
                    <input
                        type="text"
                        name="population"
                        value={formState.population}
                        onChange={handleChange}
                    />

                </label>


                <label htmlFor="email">
                    Land Area
                    <input
                        type="text"
                        name="land_area"
                        value={formState.land_area}
                        onChange={handleChange}
                    />

                </label>




                <button type="submit">
                    Add Country
                </button>
            </form>


        </div>
    );
}
